mapper.select:
SELECT A.UID As uid, A.SEQ_NO As seqNo, A.CHG_TMS_CNT As chgTmsCnt, A.GDS_SEQNO As gdsSeqno, A.GDECD As gdecd, A.GDS_NM As gdsNm, A.RMK As rmk, B.ATTR_NAME As modfMarkname FROM ET_CUS_EXG A
LEFT JOIN BSSP_COD.COD_STD_CODES B ON B.ATTR_VALUE = A.MODF_MARKCD AND B.DICTIONARY_VALUE='MODF_MARK'
1=1
AND A.SEQ_NO = #{seqNo}
AND A.CHG_TMS_CNT = #{chgTmsCnt,jdbcType=INTEGER}
AND A.ET_ARCRP_NO = #{etArcrpNo}

SELECT MAX(GDS_SEQNO) AS gdsSeqno
FROM ET_CUS_EXG
WHERE SEQ_NO = #{seqNo}
