mapper.select:
SELECT

FROM EMS_CUS_BSC A
LEFT JOIN BSSP_COD.COD_CUS_CUSTOMSFEC B ON B.CUSTOMS_CODE = A.MASTER_CUSCD 
LEFT JOIN BSSP_COD.COD_STD_AREA_CODE C ON C.AREA_CODE=A.AREA_CODE 
LEFT JOIN BSSP_COD.COD_STD_CODES D ON D.ATTR_VALUE = A.CHK_STATUS AND D.DICTIONARY_VALUE='CHK_STATUS' 
LEFT JOIN BSSP_COD.COD_STD_CODES E ON E.ATTR_VALUE=A.DCL_TYPECD AND E.DICTIONARY_VALUE='DCL_TYPE' 
LEFT JOIN BSSP_COD.COD_STD_CODES F ON F.ATTR_VALUE=A.EMS_TYPECD AND F.DICTIONARY_VALUE='EMS_TYPE' 

    1=1

        AND A.EMS_TYPECD = #{emsTypecd} 


        AND A.INPUT_COP_NO = #{inputCopNo} 


        AND A.EMS_NO LIKE '%${emsNo}%'


        AND A.ETPS_PREENT_NO LIKE '%${etpsPreentNo}%' 


        AND A.DCL_TYPECD = #{dclTypecd} 


        AND A.CHK_STATUS = #{chkStatus} 


        AND A.FINISH_VALID_DATE >= #{finishValidDateStart} 


        AND A.FINISH_VALID_DATE <= '${finishValidDateEnd} 23:59:59' 


        AND A.DEC_TIME >= #{decTimeStart} 


        AND A.DEC_TIME <= '{decTimeEnd}  23:59:59'

SELECT EMS_NO AS id,EMS_NO AS text FROM EMS_CUS_BSC A

    FINISH_VALID_DATE>=CURDATE()

        AND A.INPUT_COP_NO = #{inputCopNo} 


        AND A.EMS_TYPECD = #{emsTypecd}
