mapper.select:
SELECT

FROM BSSP_EMS.EMS_PUTREC_UCNS_DT A
LEFT JOIN BSSP_COD.COD_STD_CODES B ON B.ATTR_VALUE = A.UCNS_DCL_STUCD AND
B.DICTIONARY_VALUE='UCNS_DCL_STUCD'  
LEFT JOIN BSSP_COD.COD_STD_CODES C ON C.ATTR_VALUE=A.MODF_MARKCD AND C.DICTIONARY_VALUE='MODF_MARK'  
LEFT JOIN BSSP_COD.COD_STD_CODES D ON D.ATTR_VALUE=A.ETPS_EXE_MARKCD AND
D.DICTIONARY_VALUE='ETPS_EXE_MARK'  
LEFT JOIN (SELECT DISTINCT SEQ_NO,GDS_SEQNO,GDS_MTNO,GDECD,GDS_NM FROM EMS_PUTREC_IMG WHERE MODF_MARKCD='3' AND SEQ_NO = #{seqNo}
) E ON A.MTPCK_SEQNO=E.GDS_SEQNO AND A.SEQ_NO=E.SEQ_NO
LEFT JOIN (SELECT DISTINCT SEQ_NO,GDS_SEQNO,GDS_MTNO,GDECD,GDS_NM FROM EMS_PUTREC_EXG WHERE MODF_MARKCD='3' AND SEQ_NO = #{seqNo}
) F ON A.ENDPRD_SEQNO=F.GDS_SEQNO AND A.SEQ_NO=F.SEQ_NO
WHERE A.SEQ_NO = #{seqNo} AND A.CHG_TMS_CNT = #{chgTmsCnt}
