mapper.select:
		SELECT
		C.ATTR_NAME AS CHKSTATUSNAME,

		FROM EMS_BWS_BSC A
		LEFT JOIN BSSP_COD.COD_STD_CODES C ON C.ATTR_VALUE = A.CHK_STATUS AND C.DICTIONARY_VALUE='CHK_STATUS' 
		LEFT JOIN BSSP_COD.COD_STD_CODES D ON D.ATTR_VALUE=A.EMAPV_STUCD AND D.DICTIONARY_VALUE='EMAPV_STUCD_BWS'  
		LEFT JOIN bssp_cod.COD_STD_CODES E ON E.ATTR_VALUE=A.DCL_TYPECD AND E.DICTIONARY_VALUE='DCL_TYPECD_BWS' 

            1=1

				AND A.BWS_NO LIKE '%${bwsNo}%' 


				AND A.ETPS_PREENT_NO LIKE '%${etpsPreentNo}%'


				AND A.BIZOP_ETPS_NM LIKE '%${bizopEtpsNm}%'


                AND A.DCL_TYPECD = #{dclTypecd}


                AND A.EMAPV_STUCD = #{emapvStucd}


				AND A.CHK_STATUS = #{chkStatus} 


				AND A.FINISH_VALID_DATE >= #{finishValidDateStart} 


				AND A.FINISH_VALID_DATE <= '${finishValidDateEnd} 23:59:59' 


				AND A.DEC_TIME >= #{decTimeStart} 



				AND A.DEC_TIME <= '${decTimeEnd}  23:59:59' 


				AND A.MASTER_CUSCD = #{masterCuscd} 


				AND A.AREA_CODE = #{areaCode} 


				AND A.INPUT_COP_NO = #{inputCopNo}

SELECT BWS_NO AS id,BWS_NO AS text FROM EMS_BWS_BSC A

	FINISH_VALID_DATE>=CURDATE() AND CHK_STATUS='P'

		AND A.INPUT_COP_NO = #{inputCopNo}
