mapper.select:
SELECT
FROM INV_CUS_BSC A LEFT JOIN BSSP_COD.COD_CUS_CUSTOMSFEC B ON B.CUSTOMS_CODE = A.MASTER_CUSCD LEFT JOIN BSSP_COD.COD_STD_CODES D ON D.ATTR_VALUE = A.CHK_STATUS AND D.DICTIONARY_VALUE='CHK_STATUS' LEFT JOIN bssp_cod.COD_STD_CODES E ON E.ATTR_VALUE=A.INVT_STUCD AND E.DICTIONARY_VALUE='EMAPV_MARKCD_SAS' LEFT JOIN bssp_cod.COD_STD_CODES F ON F.ATTR_VALUE=A.I_E_FLAG AND F.DICTIONARY_VALUE='DIRECTION_TYPECD' LEFT JOIN bssp_cod.COD_STD_CODES G ON G.ATTR_VALUE=A.BOND_INVT_TYPECD AND G.DICTIONARY_VALUE='BOND_INVT_TYPECD' LEFT JOIN bssp_cod.COD_CUS_TRADE H ON H.TRADE_MODE=A.SUPV_MODECD LEFT JOIN bssp_cod.COD_CUS_CUSTOMSFEC I ON I.CUSTOMS_CODE=A.DCL_PLC_CUSCD
1=1
AND A.INPUT_COP_NO = #{inputCopNo}
AND A.DOC_TYPE = #{docType}
AND A.I_E_FLAG = #{iEFlag}
AND A.I_E_FLAG1 = #{iEFlag1}
AND A.BOND_INVT_NO LIKE '%${bondInvtNo}%'
AND A.PUTREC_NO LIKE '%${putrecNo}%'
AND A.CHK_STATUS = #{chkStatus}
AND A.BOND_INVT_TYPECD = #{bondInvtTypecd}
AND A.ETPS_INNER_INVT_NO LIKE '%${etpsInnerInvtNo}%'
AND A.DEC_TIME >= #{decTimeStart}
AND A.DEC_TIME <= '${decTimeEnd} 23:59:59'
AND NOT EXISTS (SELECT 1 FROM BSSP_SAS.SAS_PASSPORT_RLT WHERE RLT_NO=A.SEQ_NO) AND A.DOC_TYPE NOT IN('A0604','A0605')
ORDER BY A.INVT_DCL_TIME DESC
