mapper.select:
SELECT A.UID AS UID,SAS_DCL_NO AS SASDCLNO,CHG_TMS_CNT AS CHGTMSCNT,MASTER_CUSCD AS MASTERCUSCD, SAS_DCL_PREENT_NO AS SASDCLPREENTNO,A.DCL_TYPECD AS DCLTYPECD,B.ATTR_NAME AS DCLTYPENM,BUSINESS_TYPECD AS BUSINESSTYPECD, D.ATTR_NAME AS DIRECTIONTYPECD,AREAIN_ORIACT_NO AS AREAINORIACTNO,AREAOUT_ORIACT_NO AS AREAOUTORIACTNO, AREAIN_ETPSNO AS AREAINETPSNO,AREAIN_ETPS_NM AS AREAINETPSNM,AREAIN_ETPS_SCCD AS AREAINETPSSCCD, AREAOUT_ETPSNO AS AREAOUTETPSNO,AREAOUT_ETPS_NM AS AREAOUTETPSNM,AREAOUT_ETPS_SCCD AS AREAOUTETPSSCCD, DPST_LEVY_BL_NO AS DPSTLEVYBLNO,VALID_TIME AS VALIDTIME,EXHIBITION_PLACE AS EXHIBITIONPLACE,DCL_ER AS DCLER, DCL_TIME AS DCLTIME,PUTREC_EMAPV_TIME AS PUTRECEMAPVTIME,CHG_EMAPV_TIME AS CHGEMAPVTIME,CLS_CASE_TIME AS CLSCASETIME, EMAPV_MARKCD AS EMAPVMARKCD,C.ATTR_NAME AS DCLTBSTUCD,OWNER_SYSTEM AS OWNERSYSTEM,RMK AS RMK, ETPS_PREENT_NO AS ETPSPREENTNO,COP_ENT_NO AS COPENTNO,DECLARE_MARK AS DECLAREMARK,AREA_CODE AS AREACODE, DOC_TYPE AS DOCTYPE,CHK_STATUS AS CHKSTATUS,E.ATTR_NAME AS CHKSTATUSNM,RET_CHANNEL AS RETCHANNEL,A.INPUT_COP_NO AS INPUTCOPNO, A.INPUT_COP_NAME AS INPUTCOPNAME,A.INPUTER_NAME AS INPUTERNAME,A.INPUTER_CODE AS INPUTERCODE,A.DEC_TIME AS DECTIME, A.SEQ_NO AS SEQNO,A.CHK_TIME AS CHKTIME,A.CREATE_BY AS CREATEBY,A.CREATE_NAME AS CREATENAME,A.CREATE_TIME AS CREATETIME, A.UPDATE_BY AS UPDATEBY,A.UPDATE_NAME AS UPDATENAME,A.UPDATE_TIME AS UPDATETIME,A.BIZ_TYPE AS BIZTYPE,A.EMAPV_DESCRIPTION AS EMAPVDESCRIPTION, A.COL1 AS COL1,A.COL2 AS COL2,A.COL3 AS COL3,A.COL4 AS COL4 FROM SAS_DCL_HIS_BSC A LEFT JOIN BSSP_COD.COD_STD_CODES B ON B.ATTR_VALUE = A.DCL_TYPECD AND B.DICTIONARY_VALUE='DCL_TYPECD_SAS' LEFT JOIN BSSP_COD.COD_STD_CODES C ON C.ATTR_VALUE = A.DCL_TB_STUCD AND C.DICTIONARY_VALUE='DCL_TB_STUCD' LEFT JOIN BSSP_COD.COD_STD_CODES D ON D.ATTR_VALUE = A.DIRECTION_TYPECD AND D.DICTIONARY_VALUE='DIRECTION_TYPECD' LEFT JOIN BSSP_COD.COD_STD_CODES E ON E.ATTR_VALUE = A.CHK_STATUS AND E.DICTIONARY_VALUE='CHK_STATUS'
1=1
AND A.INPUT_COP_NO = #{inputCopNo}
AND A.SAS_DCL_NO LIKE '%${sasDclNo}%'
AND A.SAS_DCL_PREENT_NO LIKE '%${sasDclPreentNo}%'
AND A.ETPS_PREENT_NO LIKE '%${etpsPreentNo}%'
AND A.DCL_TYPECD = #{dclTypecd}
AND A.VALID_TIME >= #{validTimeStart}
AND A.VALID_TIME <= #{validTimeEnd}
AND A.EMAPV_MARKCD = #{emapvMarkcd}
AND A.CHK_STATUS = #{chkStatus}
AND A.DEC_TIME >= #{decTimeStart}
AND A.DEC_TIME <= '${decTimeEnd} 23:59:59'
AND (A.AREAIN_ETPS_NM LIKE '%${areainEtps}%' or A.AREAIN_ETPSNO LIKE '%${areainEtps}%')
AND A.AREAIN_ORIACT_NO LIKE '%${areainOriactNo}%'
AND A.DIRECTION_TYPECD = #{directionTypecd}
AND A.DOC_TYPE = #{docType}
AND A.BUSINESS_TYPECD = #{businessTypecd}
