mapper.select:
SELECT A.UID AS UID,SAS_DCL_NO AS SASDCLNO,SAS_DCL_SEQNO AS SASDCLSEQNO, CHG_TMS_CNT AS CHGTMSCNT,MTPCK_ENDPRD_TYPECD AS MTPCKENDPRDTYPECD,ORIACT_GDS_SEQNO AS ORIACTGDSSEQNO, GDECD AS GDECD,GDS_NM AS GDSNM,GDS_SPCF_MODEL_DESC AS GDSSPCFMODELDESC,DCL_QTY AS DCLQTY, B.UNIT_NAME AS DCLUNITCD,DCL_UPRC_AMT AS DCLUPRCAMT,DCL_TOTAL_AMT AS DCLTOTALAMT,C.CURR_NAME AS DCLCURRCD, LICENCE_NO AS LICENCENO,LICENCE_VALID_TIME AS LICENCEVALIDTIME,ACTL_AREAOUT_QTY AS ACTLAREAOUTQTY, ACTL_AREAIN_QTY AS ACTLAREAINQTY,GDS_MARKCD AS GDSMARKCD,GDS_RMK AS GDSRMK,INVT_VDEDT_QTY AS INVTVDEDTQTY, RCNT_VDEDT_TIME AS RCNTVDEDTTIME,MODF_MARKCD AS MODFMARKCD,RMK AS RMK,ETPS_PREENT_NO AS ETPSPREENTNO, A.SEQ_NO AS SEQNO,A.COL1 AS COL1,A.COL2 AS COL2,A.COL3 AS COL3,A.COL4 AS COL4,D.ATTR_NAME As modfMarkname FROM SAS_DCL_HIS_DT A LEFT JOIN BSSP_COD.COD_CUS_UNIT B ON B.UNIT_CODE = A.DCL_UNITCD LEFT JOIN BSSP_COD.COD_CUS_CURR C ON C.CURR_CODE = A.DCL_CURRCD LEFT JOIN BSSP_COD.COD_STD_CODES D ON D.ATTR_VALUE = A.MODF_MARKCD AND D.DICTIONARY_VALUE='MODF_MARK'
1=1
AND A.SEQ_NO = #{seqNo}
AND A.CHG_TMS_CNT = #{chgTmsCnt}
AND A.SAS_DCL_NO = #{sasDclNo}
AND A.MODF_MARKCD != #{modfMarkcd}
AND A.GDECD LIKE '%${gdecd}%'
AND A.SAS_DCL_SEQNO LIKE '%${sasDclSeqno}%'
AND A.GDS_NM LIKE '%${gdsNm}%'
