mapper.select:
SELECT

FROM BSSP_SAS.SAS_DRAGCAR_CUS A
LEFT JOIN BSSP_COD.COD_STD_CODES C ON C.ATTR_VALUE =A.CHK_STATUS AND C.DICTIONARY_VALUE='CHK_STATUS' 
LEFT JOIN BSSP_COD.COD_STD_CODES D ON D.ATTR_VALUE=A.BIZ_TYPE AND D.DICTIONARY_VALUE='SAS_TYPE'  
LEFT JOIN BSSP_COD.COD_STD_CODES E ON A.DCL_TYPECD=E.ATTR_VALUE AND E.DICTIONARY_VALUE='DCL_TYPE'

	1=1

		AND A.VEHICLE_FRAME_NO LIKE '%${vehicleFrameNo}%' 


		AND A.ETPS_PREENT_NO LIKE '%${etpsPreentNo}%' 


		AND A.DCl_TYPECD = #{dclTypecd} 


		AND A.CHK_STATUS = #{chkStatus} 


		AND A.ETPS_NM = #{etpsNm}


		AND A.BIZ_TYPE = #{bizType} 


		AND A.AREA_CODE = #{areaCode} 


		AND A.DEC_TIME >=
		#{decTimeStart} 



		AND A.DEC_TIME <=
		'${decTimeEnd} 23:59:59'
