mapper.select:
SELECT
WHERE 1=1
AND A.IC_CODE LIKE '%${icCode}%'
AND A.FIXED_CARD_REGNO LIKE '%${fixedCardRegno}%'
AND A.REG_DATE >= #{regDateStart}
AND A.REG_DATE <= '${regDateEnd} 23:59:59'
AND A.VEHICLE_NO LIKE '%${vehicleNo}%'
AND A.VEHICLE_SORT = #{vehicleSort}
AND A.VALID_DATE >= #{validDateStart}
AND A.VALID_DATE <= '${validDateEnd} 23:59:59'
AND A.FIXED_CARD_STATUS = #{fixedCardStatus}
AND A.USE_ETPS_NAME LIKE '%${useEtpsName}%'
AND A.USE_ETPS_CODE LIKE '%${useEtpsCode}%'
